<?phprequire_once('./../../global.php');$slug = Filter::text($_GET['slug']);	// check project$project = Project::getProjectFromSlug($slug);if($project == null) {	Session::setMessage('That project does not exist.');	header('Location: '.Url::error());	exit();}$relationship = Filter::alphanum($_GET['relationship']);if($relationship == "organizers") {	$organizers = ProjectUser::getProjectUsersByRelationship(		$project->getID(),		ProjectUser::ORGANIZER	);	$json = array();	$json[] = $project->getCreator()->getUserName();	foreach($organizers as $o) {		$json[] = $o->getUserName();	}	echo json_encode($json);}