<?phprequire_once('./../../global.php');$slug = Filter::text($_GET['slug']);	// check project$project = Project::getProjectFromSlug($slug);if($project == null) {	Session::setMessage('That project does not exist.');	header('Location: '.Url::error());	exit();}$relationship = Filter::text($_GET['relationship']);if($relationship == "unaffiliated") {	$usernames = $project->getUnaffiliatedUsernames();} elseif($relationship == 'bannable') {	$usernames = $project->getBannableUsernames();} elseif($relationship == 'trusted') {	$usernames = $project->getTrustedUsernames();}echo json_encode($usernames);