<?phprequire_once TEMPLATE_PATH.'/site/helper/format.php';$uploads = $SOUP->get('uploads');$empty = $SOUP->get('empty', '');echo '<div class="upload-wrapper">';if($uploads != null) {	foreach($uploads as $upload) {		$downloadUrl = Url::download($upload->getID());		echo '<div class="upload">';		if($upload->hasThumb()) {			$thumbUrl = Url::thumb().'/'.$upload->getStoredName();			echo '<a class="thumb" style="background-image:url('.$thumbUrl.')" href="'.$downloadUrl.'">&nbsp;</a>';		} else {			echo '<a class="icon" href="'.$downloadUrl.'">&nbsp;</a>';		}		echo '<a class="name" href="'.$downloadUrl.'">'.$upload->getOriginalName().'</a> ';		echo '<span class="size">'.formatFileSize($upload->getSize()).'</span>';		echo '</div>';	}	echo '<br style="clear: both;"/>';} else {	echo $empty;}echo '</div>';