<?php$project = $SOUP->get('project');$task = $SOUP->get('task');$events = $SOUP->get('events');$comments = $SOUP->get('comments', array());//$uploads = $SOUP->get('uploads');$fork = $SOUP->fork();$fork->set('project', $project);$fork->set('pageTitle', $project->getTitle());$fork->set('headingURL', Url::project($project->getID()));$fork->set('selected', "tasks");$fork->set('breadcrumbs', Breadcrumbs::task($task->getID()));$fork->startBlockSet('body');?><div class="left"><?php	$SOUP->render('project/partial/task', array(		'id' => 'task',		'title' => 'Task Info',		'numComments' => count($comments)		));?><?php	$SOUP->render('site/partial/newUploads', array(	//	'uploads' => $uploads	));?><?php	$SOUP->render('project/partial/comments', array(		'comments' => $comments,		'processURL' => Url::taskProcess($task->getID()),		'parentID' => $task->getID(),		'size' => 'large',		//'class' => 'hidden',		'id' => 'comments'		));?></div><div class="right"><?php	$SOUP->render('project/partial/taskCrew', array(	//	'id' => 'accepted',		'size' => 'small'		));?><?php	$SOUP->render('project/partial/taskProgress', array(	//	'id' => 'accepted',		'size' => 'small'	));?><?php	$SOUP->render('site/partial/activity', array(		'title' => "Recent Activity",		'size' => 'small',		'events' => $events,		'olderURL' => Url::activityTasks($project->getID())		));?></div><?php$fork->endBlockSet();$fork->render('site/partial/page');