<?php$project = $SOUP->get('project');$task = $SOUP->get('task');//$user = $SOUP->get('user');//$updates = $SOUP->get('updates');//$accepted = $SOUP->get('accepted');$events = $SOUP->get('events');$comments = $SOUP->get('comments');$update = $SOUP->get('update');$fork = $SOUP->fork();$fork->set('project', $project);$fork->set('pageTitle', $project->getTitle());$fork->set('headingURL', Url::project($project->getID()));$fork->set('selected', "tasks");$fork->set('breadcrumbs', Breadcrumbs::update($update->getID()));$fork->startBlockSet('body');?><div class="left"><?php	$SOUP->render('project/partial/update', array(		'size' => 'large'	));?><?php	$SOUP->render('site/partial/newUploads', array(	//	'uploads' => $uploads	));?><?php	$SOUP->render('project/partial/comments', array(		'comments' => $comments,		'processURL' => Url::updateProcess($update->getID()),		'parentID' => $update->getID(),		'size' => 'large'	));?></div><div class="right"><?php	$SOUP->render('project/partial/tasks', array(		'title' => 'Task Info',		'tasks' => array($task),		'size' => 'small'	));?><?php	$SOUP->render('project/partial/updates', array(		'id' => 'updates',		'size' => 'small',		'title' => 'More Task Updates',		'taskUpdates' => false	));?><?php	$SOUP->render('site/partial/activity', array(		'title' => "Recent Activity",		'events' => $events,		'size' => 'small',		'olderURL' => Url::activityTasks($project->getID())		));?></div><?$fork->endBlockSet();$fork->render('site/partial/page');