<?phpinclude_once TEMPLATE_PATH.'/site/helper/format.php';$project = $SOUP->get('project');$banned = $SOUP->get('banned');// only organizers or creator may ban/unban users$hasPermission = ( Session::isAdmin() ||					ProjectUser::isOrganizer(Session::getUserID(), $project->getID()) ||					ProjectUser::isCreator(Session::getUserID(), $project->getID()) );$fork = $SOUP->fork();$fork->set('id', 'banned');$fork->set('title', 'Banned');$fork->set('creatable', $hasPermission);$fork->set('createLabel', 'Ban Users');$fork->set('editable', $hasPermission);$fork->set('editLabel', 'Edit Banned');$fork->startBlockSet('body');?><?php if($hasPermission): ?><script type="text/javascript">$(document).ready(function(){	$("#banned .editButton").click(function(){		var buttons = $("#banned div.view input[type='button']");		if($(buttons).is(":hidden")) {			$(buttons).fadeIn();		} else {			$(buttons).fadeOut();		}	});		$("#banned .createButton").click(function(){		var newBan = $("#banned .newBan");		var view = $("#banned .view");		toggleEditView(view, newBan);		if($(view).is(":hidden"))			$('#txtNewBan').focus();	});		$("#btnCancelBan").click(function(){		$("#banned .newBan").hide();		$("#banned .view").fadeIn();	});		$("#banned input.unban").click(function(){		var id = $(this).attr('id').substring(6);		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info': {				'action': 'unban',				'userID': id			},			'buttonID': $(this)		});	});	$( "#txtNewBan" )	// don't navigate away from the field on tab when selecting an item	.bind( "keydown", function( event ) {		if ( event.keyCode === $.ui.keyCode.TAB &&				$( this ).data( "autocomplete" ).menu.active ) {			event.preventDefault();		}	})	.autocomplete({		source: function( request, response ) {			$.getJSON( '<?= Url::peopleSearch($project->getID()) ?>/not-affiliated', {				term: extractLast( request.term )			}, response );		},		search: function() {			// custom minLength			var term = extractLast( this.value );			if ( term.length < 2 ) {				return false;			}		},		focus: function() {			// prevent value inserted on focus			return false;		},		select: function( event, ui ) {			var terms = split( this.value );			// remove the current input			terms.pop();			// add the selected item			terms.push( ui.item.value );			// add placeholder to get the comma-and-space at the end			terms.push( "" );			this.value = terms.join( ", " );			return false;		}	});			$('#btnNewBan').click(function() {		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info': {				'action': 'new-bans',				'banlist': $('#txtNewBan').val()			},			'buttonID': '#btnNewBan'		});	});	});</script><div class="newBan hidden">	<div class="clear">		<label for="txtNewBan">People to Ban</label>		<div class="input">			<input type="text" id="txtNewBan" />			<p>Usernames separated by commas</p>		</div>	</div>	<div class="clear">		<div class="input">			<input type="button" id="btnNewBan" value="Ban" />			<input type="button" id="btnCancelBan" value="Cancel" />		</div>	</div></div><?php endif; ?><div class="view"><?php if($banned != null) {	echo '<ul class="segmented-list users">';	foreach($banned as $b) {		echo '<li>';		echo '<input id="unban-'.$b->getID().'" type="button" class="unban hidden" value="Unban" />';		echo formatUserPicture($b->getID(), 'small');		echo '<h6 class="primary">'.formatUserLink($b->getID()).'</h6>';		echo '<p class="secondary">banned</p>';		echo '</li>';	}	echo '</ul>';} else {	echo '<p>(none)</p>';}?></div><?php$fork->endBlockSet();$fork->render('site/partial/panel');