<?phpinclude_once TEMPLATE_PATH.'/site/helper/format.php';$project = $SOUP->get('project');$contributors = $SOUP->get('contributors');$fork = $SOUP->fork();$fork->set('id', 'contributors');$fork->set('title', 'Contributors');$fork->set('creatable', true);$fork->set('createLabel', 'Invite');$fork->set('editable', true);$fork->startBlockSet('body');?><script type="text/javascript">$(document).ready(function(){	$("#contributors .editButton").click(function(){		var buttons = $("#contributors div.view input[type='button']");		if($(buttons).is(":hidden")) {			$(buttons).fadeIn();		} else {			$(buttons).fadeOut();		}	});		$("#contributors .createButton").click(function(){		var invite = $("#contributors .invite");		var view = $("#contributors .view");		toggleEditView(view, invite);		if($(view).is(":hidden"))			$('#txtInviteContributors').focus();	});		$("#btnCancelContributors").click(function(){		$("#contributors .invite").hide();		$("#contributors .view").fadeIn();	});		$("#contributors input.ban").click(function(){		var id = $(this).attr('id').substring(4);		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info': {				'action': 'ban',				'userID': id			},			'buttonID': $(this)		});	});		$("#contributors input.organizer").click(function(){		var id = $(this).attr('id').substring(10);		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info': {				'action': 'make-organizer',				'userID': id			},			'buttonID': $(this)		});	});});</script><div class="view"><?php if($contributors != null) {	echo '<ul class="segmented-list users">';	foreach($contributors as $c) {		echo '<li>';		echo '<input id="ban-'.$c->getID().'" type="button" class="ban hidden" value="Ban" />';		echo '<input id="organizer-'.$c->getID().'" type="button" class="organizer hidden" value="Make Organizer" />';		echo formatUserPicture($c->getID(), 'small');		echo '<h6 class="primary">'.formatUserLink($c->getID()).'</h6>';		echo '<p class="secondary">contributor</p>';		echo '</li>';	}	echo '</ul>';} else {	echo '<p>(none)</p>';}?></div><div class="invite hidden">	<div class="clear">		<label for="txtInviteContributors">People to Invite</label>		<div class="input">			<input type="text" id="txtInviteContributors" />			<p>Usernames or email addresses, separated by commas</p>		</div>	</div>	<div class="clear">		<div class="input">			<input type="button" id="btnInviteContributors" value="Invite" />			<input type="button" id="btnCancelContributors" value="Cancel" />		</div>	</div></div><?php$fork->endBlockSet();$fork->render('site/partial/panel');