<?php$fork = $SOUP->fork();$fork->set('title', 'New Project');$fork->startBlockSet('body');?><script type="text/javascript">$(document).ready(function(){	$('#txtDeadline').datepicker({			changeMonth: true,			changeYear: true,			dateFormat: 'yy-mm-dd' // MySQL datetime format		});		$('#btnCreateProject').click(function(){		buildPost({			'processPage': '<?= Url::projectNewProcess() ?>',			'info': $('#frmNewItem').serialize(),			'buttonID': 'btnCreateProject'		});	});		$('#txtTitle').focus();});</script><!--p>All you need for a new <?= PIPELINE_NAME ?> project is a title and a pitch (description). The other fields &mdash; specs, rules, and deadline &mdash; are recommended, but optional. We've provided suggestions, but feel free to change or remove them.</p--><!--div class="line"> </div--><form id="frmNewItem"><div class="clear">	<label for="txtTitle">Title<span class="required">*</span></label>	<div class="input">		<input id="txtTitle" name="txtTitle" type="text" maxlength="255" />		<p>The name of your project</p>	</div></div><div class="clear">	<label for="txtPitch">Pitch<span class="required">*</span></label>	<div class="input">		<textarea id="txtPitch" name="txtPitch"></textarea>		<p>Describe your project idea. What is the project about? How will people work together?</p>		<p><a href="<?= Url::help() ?>#help-html-allowed">Some HTML allowed</a></p>	</div></div><div class="clear">	<label for="txtSpecs">Specs</label>	<div class="input">		<textarea id="txtSpecs" name="txtSpecs">Dimensions: 550px by 400pxFramerate: 24 fpsBackground: whiteMax length: 60 sec eachFormats: AVI, MPG, Quicktime, FlashBreak your fontsPut each contribution in a movie clip		</textarea>		<p>Technical specifications for this project</p>		<p>One spec per line; put a colon (:) in between name/value pairs</p>	</div></div><div class="clear">	<label for="txtRules">Rules</label>	<div class="input">		<textarea id="txtRules" name="txtRules">+Multiple contributions allowed+Dialogue, sound effects, music allowed-Low quality contributions won't be included-No extreme violence-No nudity-No profanity		</textarea>		<p>Rules and guidelines for this project</p>		<p>One rule per line; put a plus (+) in front of "do's" and a minus (-) in front of "don'ts"</p>	</div></div><div class="clear">	<label for="txtDeadline">Deadline</label>	<div class="input">		<input id="txtDeadline" name="txtDeadline" type="text" />		<p>When the project should be finished</p>	</div></div><div class="clear">	<label for="chkPrivate">Private</label>	<div class="input">		<input type="checkbox" id="chkPrivate" name="chkPrivate" value="private" />		<p>If checked, this project will be hidden from everyone except members and invitees.</p>	</div></div><div class="clear">	<div class="input">		<input id="btnCreateProject" type="button" value="Create Project" />	</div></div></form><?php$fork->endBlockSet();$fork->render('site/partial/panel');