<?phpinclude_once TEMPLATE_PATH.'/site/helper/format.php';$project = $SOUP->get('project');$organizers = $SOUP->get('organizers');$fork = $SOUP->fork();$fork->set('id', 'organizers');$fork->set('title', 'Organizers');$fork->set('creatable', true);$fork->set('createLabel', 'Invite');$fork->set('editable', true);$fork->startBlockSet('body');?><script type="text/javascript">$(document).ready(function(){	$("#organizers .editButton").click(function(){		var revoke = $("#organizers input.revoke");		if($(revoke).is(":hidden")) {			$(revoke).fadeIn();		} else {			$(revoke).fadeOut();		}	});		$("#organizers .createButton").click(function(){		var invite = $("#organizers .invite");		var view = $("#organizers .view");		toggleEditView(view, invite);		if($(view).is(":hidden"))			$('#txtInviteOrganizers').focus();	});		$("#btnCancelOrganizers").click(function(){		$("#organizers .invite").hide();		$("#organizers .view").fadeIn();	});		$("#organizers input.revoke").click(function(){		var id = $(this).attr('id').substring(7);		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info': {				'action': 'revoke-organizer',				'userID': id			},			'buttonID': $(this)		});	});});</script><div class="view"><?php if($organizers != null) {	echo '<ul class="segmented-list users">';	foreach($organizers as $o) {		echo '<li>';		echo '<input id="revoke-'.$o->getID().'" type="button" class="revoke hidden" value="Revoke Organizer" />';		echo formatUserPicture($o->getID(), 'small');		echo '<h6 class="primary">'.formatUserLink($o->getID()).'</h6>';		echo '<p class="secondary">organizer</p>';		echo '</li>';	}	echo '</ul>';} else {	echo '<p>(none)</p>';}?></div><div class="invite hidden">	<div class="clear">		<label for="txtInviteOrganizers">People to Invite</label>		<div class="input">			<input type="text" id="txtInviteOrganizers" />			<p>Usernames or email addresses, separated by commas</p>		</div>	</div>	<div class="clear">		<div class="input">			<input type="button" id="btnInviteOrganizers" value="Invite" />			<input type="button" id="btnCancelOrganizers" value="Cancel" />		</div>	</div></div><?php$fork->endBlockSet();$fork->render('site/partial/panel');