<?phpinclude_once TEMPLATE_PATH.'/site/helper/format.php';$project = $SOUP->get('project');$fork = $SOUP->fork();$fork->set('title', "Specs");$fork->set('id', "specs");$fork->set('editable', true);$fork->set('editLabel', 'Edit Specs');$fork->startBlockSet('body');$formattedSpecs = formatSpecs($project->getSpecs());?><script type="text/javascript">$(document).ready(function(){	$("#btnSaveSpecs").mousedown(function(){		buildPost({			'processPage':'<?= Url::detailsProcess($project->getID()) ?>',			'info':			{				'action':'specs',				'specs':$("#txtSpecs").val()			},			'buttonID':'#btnSaveSpecs'		});	});	$("#btnCancelSpecs").mousedown(function(){		$("#specs .edit").hide();		$("#specs .view").fadeIn();	});	$("#specs .editButton").click(function(){		var edit = $("#specs .edit");		var view = $("#specs .view");		toggleEditView(edit, view);		if($(view).is(":hidden"))			$('#txtSpecs').focus();	});});</script><div class="view"><?phpif(count($formattedSpecs) > 0) {	echo '<ul class="segmented-list">';	foreach($formattedSpecs as $spec)		echo '<li>'.$spec.'</li>';	echo '</ul>';} else {	echo "(empty)";}?></div><div class="edit hidden"><textarea id="txtSpecs"><?= html_entity_decode($project->getSpecs()) ?></textarea><div class="buttons">	<input id="btnSaveSpecs" class="right" type="button" value="Save" />	<input id="btnCancelSpecs" class="right" type="button" value="Cancel" />	<p class="right">One spec per line</p></div></div><?php$fork->endBlockSet();$fork->render('site/partial/panel');