<?phpinclude_once TEMPLATE_PATH.'/site/helper/format.php';$task = $SOUP->get('task', array());$fork = $SOUP->fork();$fork->set('title', 'Task Info');$fork->startBlockSet('body');?><ul class="segmented-list tasks">	<li>	<?php		// title		$title = $task->getTitle();		$url = Url::task($task->getID());		$closed = ($task->getStatus() == Task::STATUS_CLOSED) ? ' closed' : ''; // CSS class for strikethrough		echo '<p class="title'.$closed.'"><a href="'.$url.'">'.$title.'</a></p>';				echo '<p class="info">';				// status		$statusName = Task::getStatusName($task->getStatus());		if($task->getStatus() == Task::STATUS_CLOSED)			echo '<span class="bad">'.$statusName.'</span>';		else			echo '<span class="good">'.$statusName.'</span>';		// deadline		if($task->getDeadline() != '')			echo ' <span class="slash">/</span> due '.formatTimeTag($task->getDeadline());					echo '</p>';	?>		</li></ul><?php$fork->endBlockSet();$fork->render('site/partial/panel');