<?php$project = $SOUP->get('project');?><script type="text/javascript">$(document).ready(function(){		<?php if($project->isFollower(Session::getUserID())): ?>		$('#btnUnfollow').click(function(){		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info':{				'action': 'unfollow'			},			'buttonID': 'btnUnfollow'		});	});			<?php elseif(!$project->isAffiliated(Session::getUserID())): ?>		$('#btnFollow').click(function(){		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info':{				'action': 'follow'			},			'buttonID': 'btnFollow'		});	});				<?php endif; ?>});</script><div class="your-role"><?phpif(!Session::isLoggedIn()) {	echo '<p>You must <a href="'.Url::logIn().'"><strong>log in</strong></a> to contribute.</p>';} elseif($project->isCreator(Session::getUserID())) {	echo '<p>You <strong>created</strong> this project.</p>';} elseif( ($project->isContributor(Session::getUserID())) && ($project->isTrusted(Session::getUserID())) ) {	echo '<p>You are a <strong><a href="'.Url::help().'">trusted</a> contributor</strong> to this project.</p>';} elseif($project->isContributor(Session::getUserID())) {	echo '<p>You are a <strong>contributor</strong> to this project.</p>';} elseif( ($project->isFollower(Session::getUserID())) && ($project->isTrusted(Session::getUserID())) ) {	echo '<p>You are a <strong><a href="'.Url::help().'">trusted</a> follower</strong> of this project.</p>';	echo '<input id="btnUnfollow" type="button" value="Unfollow" />';} elseif($project->isFollower(Session::getUserID())) {	echo '<p>You are <strong>following</strong> this project.</p>';	echo '<input id="btnUnfollow" type="button" value="Unfollow" />';	} elseif($project->isBanned(Session::getUserID())) {	echo '<p>You are <strong>banned</strong> from this project.</p>';} elseif(Invitation::hasInvitations(Session::getUserID(), $project->getID())) {	echo '<p>You have been invited to <strong>follow</strong> this project. Visit your <a href="'.Url::dashboard().'">dashboard</a> to view and respond to your invitation(s).</p>';} elseif(!$project->isAffiliated(Session::getUserID())) {		echo '<p>Like what you see? Follow this project to get updates sent to your <a href="'.Url::dashboard().'">dashboard</a>.</p>';	echo '<input id="btnFollow" type="button" value="Follow" />';}?></div>