<?php$project = $SOUP->get('project');?><script type="text/javascript">$(document).ready(function(){	<?php if(ProjectUser::isOrganizer(Session::getUserID(), $project->getID())): ?>	$('#btnResignOrganizer').click(function(){		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info':{				'action': 'resign-organizer',				'userID': '<?= Session::getUserID() ?>'			},			'buttonID': 'btnResignOrganizer'		});	});		<?php elseif(ProjectUser::isFollower(Session::getUserID(), $project->getID())): ?>		$('#btnUnfollow').click(function(){		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info':{				'action': 'unfollow',				'userID': '<?= Session::getUserID() ?>'			},			'buttonID': 'btnUnfollow'		});	});			<?php elseif(!ProjectUser::isAffiliated(Session::getUserID(), $project->getID())): ?>		$('#btnFollow').click(function(){		buildPost({			'processPage': '<?= Url::peopleProcess($project->getID()) ?>',			'info':{				'action': 'follow'			},			'buttonID': 'btnFollow'		});	});				<?php endif; ?>});</script><div class="your-role"><?phpif(!Session::isLoggedIn()) {	echo '<p>You must <a href="'.Url::logIn().'"><strong>log in</strong></a> to contribute.</p>';} elseif(!ProjectUser::isAffiliated(Session::getUserID(), $project->getID())) {		echo '<p>Like what you see?</p>';	echo '<input id="btnFollow" type="button" value="Follow This Project" />';} elseif(ProjectUser::isCreator(Session::getUserID(), $project->getID())) {	echo '<p>You <strong>created</strong> this project.</p>';} elseif(ProjectUser::isOrganizer(Session::getUserID(), $project->getID())) {	echo '<p>You are an <strong>organizer</strong> of this project.</p>';	echo '<input id="btnResignOrganizer" type="button" value="Resign" />';} elseif(ProjectUser::isContributor(Session::getUserID(), $project->getID())) {	echo '<p>You are a <strong>contributor</strong> to this project.</p>';} elseif(ProjectUser::isFollower(Session::getUserID(), $project->getID())) {	echo '<p>You are <strong>following</strong> this project.</p>';	echo '<input id="btnUnfollow" type="button" value="Unfollow" />';} elseif(ProjectUser::isBanned(Session::getUserID(), $project->getID())) {	echo '<p>You are <strong>banned</strong> from this project.</p>';}?></div>