<?phpinclude_once TEMPLATE_PATH.'/site/helper/formatEvents.php';include_once TEMPLATE_PATH.'/site/helper/format.php';$events = $SOUP->get('events', array());$size = $SOUP->get('size', 'large');$olderURL = $SOUP->get('olderURL', null);$fork = $SOUP->fork();$fork->set('title');$fork->startBlockSet('body');if($events != null){	echo '<ul class="segmented-list activity">';	foreach($events as $event)	{		echo '<li>';		echo '<a class="picture small" href="'.Url::user($event->getUser1ID()).'"><img src="'.Url::userPictureSmall($event->getUser1ID()).'" /></a>';		echo '<p class="event">'.formatEvent($event).'</p>';		echo '<p class="when '.$event->getCssClass().'">'.formatTimeTag($event->getDateCreated()).'</p>';		echo '</li>';	}	echo '</ul>';} else {	echo "<p>(none)</p>";}$fork->endBlockSet();if($olderURL != null){	$fork->startBlockSet("footer");	?><p><a href="<?= $olderURL ?>">Older Activity &raquo;</a></p>	<?php	$fork->endBlockSet();}$fork->render('site/partial/panel');