<?php$uploads = $SOUP->get('uploads');if($uploads != null) {	foreach($uploads as $u) {		echo '<div>';		switch($u->getMime()) {			case 'image/jpg':			case 'image/jpeg':			case 'image/png':			case 'image/gif':				echo '<img src="'.Url::thumb().'/'.$u->getStoredName().'" />';				break;		}		//echo '<a href="#">'.$u->getOriginalName().'</a>';		//echo '<a href="#">preview</a> <a href="#">download</a>';		echo '</div>';	}} 