<?php$id = $SOUP->get('id', '');$class = $SOUP->get('class','');$size = $SOUP->get('size', "large");$editable = $SOUP->get('editable', false);$editLabel = $SOUP->get('editLabel', 'Edit');$creatable = $SOUP->get('creatable', false);$createLabel = $SOUP->get('createLabel', 'New');$extraButton = $SOUP->get('extraButton', false);$extraButtonLabel = $SOUP->get('extraButtonLabel', '');$title = $SOUP->get('title', null);$url = $SOUP->get('url');$body = $SOUP->get('body');$footer = $SOUP->get('footer');$title = htmlentities($title);?><div <?= ($id != '')?'id="'.$id.'"':'' ?> class="panel <?= $size ?> <?= ($class != '')?$class:'' ?>">	<div class="panel-header">		<?php if($title != null): ?>			<?php if($url != null): ?>				<h4><a href="<?= $url ?>"><?= $title ?></a></h4>			<?php else: ?>				<h4><?= $title ?></h4>			<?php endif; ?>		<?php endif; ?>		<?php if($extraButton): ?>		<input class="extraButton" type="button" value="<?= $extraButtonLabel ?>" />			<?php endif; ?>				<?php if($editable): ?>		<input class="editButton" type="button" value="<?= $editLabel ?>" />		<?php endif; ?>						<?php if($creatable): ?>		<input class="createButton create" type="button" value="<?= $createLabel ?>" />			<?php endif; ?>	</div>	<div class="panel-body"><?= $body ?></div>	<?php if($footer != ''): ?>	<div class="panel-footer"><?= $footer ?></div>	<?php endif; ?></div>